INSERT INTO `recruit_ai_checklists` VALUES (60,'2026-01-07 16:24:02.182857',NULL,1,16,'2026-01-07 16:24:10.770646',1,'{\n  \"checkList\": [\n    {\n      \"question\": \"코딩 학습을 시작했으나 난관에 부딪혔거나, 완전 초보 단계에 해당하십니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 30\n    },\n    {\n      \"question\": \"단순히 코딩 문법 암기보다는 문제 해결을 위한 \'생각하는 방법\'을 배우고 싶으십니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 30\n    },\n    {\n      \"question\": \"기획, 개발, 디자인 등 IT 분야 진로 탐색에 대한 고민이 있으며, 방향성을 잡고 싶으십니까?\",\n      \"target\": \"우대사항\",\n      \"score\": 20\n    },\n    {\n      \"question\": \"협업 경험 부족에 대한 걱정이 있거나, 스터디를 통한 협업 학습 의지가 있으십니까?\",\n      \"target\": \"우대사항\",\n      \"score\": 10\n    },\n    {\n      \"question\": \"스터디 참여에 필요한 개인 노트북을 준비할 수 있습니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 10\n    }\n  ],\n  \"matchTip\": \"이 스터디는 코딩 초보자를 위한 컴퓨팅 사고력 증진에 중점을 둡니다. 지원 시 본인이 코딩 학습에 어려움을 겪는 부분과 스터디를 통해 얻고자 하는 바를 명확히 제시하여 학습 의지를 강조하는 것이 중요합니다.\"\n}'),(70,'2026-01-08 15:37:28.599581',NULL,2,13,'2026-01-08 15:37:37.600156',1,'{\n  \"checkList\": [\n    {\n      \"question\": \"사용자와 직접 상호작용하거나 매출 발생 경험이 있는 프로덕트 개발 또는 참여 경험이 있습니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 30\n    },\n    {\n      \"question\": \"단기간(예: 3개월 이내) 내 MVP 또는 실제 서비스를 기획하고 론칭까지 완료한 경험이 있습니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 25\n    },\n    {\n      \"question\": \"기획자, 디자이너 등 다양한 직군의 팀원들과 적극적으로 협업하여 프로젝트를 진행한 경험이 있습니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 20\n    },\n    {\n      \"question\": \"개발된 프로덕트의 장기적인 운영 및 사업적 성장에 대한 관심과 기여 의지가 강합니까?\",\n      \"target\": \"우대사항\",\n      \"score\": 15\n    },\n    {\n      \"question\": \"스타트업 환경에서의 프로젝트 참여 경험이 있거나, 사업적 목표를 가진 개인 또는 팀 프로젝트를 주도한 경험이 있습니까?\",\n      \"target\": \"우대사항\",\n      \"score\": 10\n    }\n  ],\n  \"matchTip\": \"단순 개발 역량을 넘어, 사용자 반응과 사업적 성과를 중시하는 \'프로덕트 중심\'의 포트폴리오와 경험을 강조하세요.\"\n}'),(95,'2026-01-08 16:05:54.582777',NULL,3,16,'2026-01-08 16:06:00.946252',4,'{\n  \"checkList\": [\n    {\"question\": \"복잡한 문제 해결 방식 학습 및 문제 해결 능력 개발에 대한 강한 의지가 있습니까?\", \"target\": \"필수역량\", \"score\": 40},\n    {\"question\": \"코딩 경험이 없거나 부족하며, 문법 위주가 아닌 \'사고 과정\' 학습에 집중하고 싶습니까?\", \"target\": \"필수역량\", \"score\": 30},\n    {\"question\": \"IT 분야(기획, 개발, 디자인 등) 진로 방향 탐색에 대한 필요성을 느끼고 있습니까?\", \"target\": \"우대사항\", \"score\": 15},\n    {\"question\": \"스터디를 통해 협업 경험을 쌓고 타인과 함께 문제를 해결하는 과정에 참여할 의지가 있습니까?\", \"target\": \"우대사항\", \"score\": 10},\n    {\"question\": \"새로운 학습을 시작하려는 의지와 꾸준히 참여하려는 성실성을 갖추고 있습니까?\", \"target\": \"필수역량\", \"score\": 5}\n  ],\n  \"matchTip\": \"해당 스터디는 코딩 경험 유무보다 문제 해결 의지와 컴퓨팅 사고 학습에 대한 열정을 중요하게 생각합니다. 지원 시 본인의 학습 목표와 \'시작하려는 동기\'를 명확히 어필하는 것이 좋습니다.\"\n}'),(55,'2026-01-08 16:48:09.515586',NULL,4,11,'2026-01-08 16:48:19.578340',1,'{\n  \"checkList\": [\n    {\n      \"question\": \"개인 또는 팀 프로젝트에서 모바일 앱 개발을 주도적으로 진행한 경험이 1년 이상입니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 40\n    },\n    {\n      \"question\": \"AI 또는 머신러닝 기술을 앱 개발 프로젝트에 적용해 본 경험이 있습니까?\",\n      \"target\": \"우대사항\",\n      \"score\": 20\n    },\n    {\n      \"question\": \"뷰티, 화장품 관련 서비스 또는 도메인에 대한 높은 이해와 관심이 있습니까?\",\n      \"target\": \"우대사항\",\n      \"score\": 15\n    },\n    {\n      \"question\": \"초기 프로젝트 성공 및 수익 공유를 목표로, 당장의 고정적인 급여 없이 기여할 의향이 있습니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 20\n    },\n    {\n      \"question\": \"비기술 직군과의 원활한 협업 및 주로 온라인으로 진행되는 회의에 적극적으로 참여할 수 있습니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 5\n    }\n  ],\n  \"matchTip\": \"본 프로젝트는 초기 단계로, 공동 창업자의 마인드로 장기적인 성장을 함께 만들어갈 의지가 중요합니다. 앱 개발 역량과 뷰티/AI 도메인에 대한 관심을 구체적인 포트폴리오로 보여주는 것이 좋습니다.\"\n}'),(80,'2026-01-08 16:56:00.109129',NULL,5,1,'2026-01-08 16:56:10.038884',1,'{\n  \"checkList\": [\n    {\n      \"question\": \"JavaScript 기반 백엔드 개발 경험이 있습니까?\",\n      \"target\": \"기술스택\",\n      \"score\": 35\n    },\n    {\n      \"question\": \"RDB 기반 ERD 설계 및 운영 도메인 데이터 모델링 경험이 있습니까?\",\n      \"target\": \"기술스택\",\n      \"score\": 25\n    },\n    {\n      \"question\": \"AWS 인프라(EC2, RDS, S3 등) 구축 또는 CI/CD 파이프라인 구성/운영 경험이 있습니까?\",\n      \"target\": \"기술스택\",\n      \"score\": 20\n    },\n    {\n      \"question\": \"운영 데이터를 기반으로 서비스 고도화에 기여한 경험이 있습니까?\",\n      \"target\": \"필수역량\",\n      \"score\": 10\n    },\n    {\n      \"question\": \"스포츠 또는 유소년 교육 도메인에 대한 높은 이해나 관심이 있습니까?\",\n      \"target\": \"우대사항\",\n      \"score\": 10\n    }\n  ],\n  \"matchTip\": \"초기 서비스 고도화 및 인프라 구축에 대한 적극적인 의지를 보이고, JavaScript 백엔드 개발, 데이터 모델링, AWS/CI/CD 관련 경험을 문제 해결 사례 중심으로 강조하는 것이 중요합니다.\"\n}');
